header {
	position: sticky;
	top: 40px;
	margin: auto;
	display: flex;
	justify-content: center;
	align-items: center;

	z-index: 9;
	border-radius: 50px;
	backdrop-filter: blur(25px);
	background-color: rgb(250 250 250 / 0.35);
	border: 1px solid #ffffff;
	box-shadow: rgba(50, 50, 93, 0.25) 0px 2px 5px -1px,
		rgba(0, 0, 0, 0.3) 0px 1px 3px -1px;

	@media (max-width: 768px) {
		width: 70vw;
		font-size: 0.8rem;
	}

	@media (min-width: 768px) {
		width: 50vw;
	}
}

main {
	margin-bottom: 15vh;
}

section {
	min-height: 100vh;
}

ol {
	display: flex;
	justify-content: space-around;
	flex-wrap: wrap;
	padding: 1rem;
	list-style: none;
}

.nav-bar {
	width: 45vw;
	border-radius: 35px;
	background-color: rebeccapurple;
	display: flex;
	flex-direction: row;
	flex-wrap: wrap;
	justify-content: space-around;
	box-shadow: rgba(50, 50, 93, 0.25) 0px 2px 5px -1px,
		rgba(0, 0, 0, 0.3) 0px 1px 3px -1px;
	background: rgb(52, 162, 255);
	background: radial-gradient(
		circle,
		rgba(218, 0, 255, 1) 0%,
		rgba(102, 51, 153, 1) 100%
	);
}

.nav {
	text-decoration: none;
	flex: 1;
	padding: 15px 20px;
	color: #fff;
	text-align: center;
	font-weight: 500;

	&:hover {
		background-color: white;
		color: rebeccapurple;
		border-radius: 50px;
		scale: 1.2;
	}
}

.nav-section {
	// background-color: rgb(255 255 255 / 0.05);

	padding: 20px 0;
	z-index: -1;
}

.nav-section .nav {
	color: rebeccapurple;

	&:hover {
		background-color: transparent;
		color: #fff;
	}
}

.hero {
	display: flex;
	align-items: center;
	justify-content: center;
	margin: auto;
}

h1 {
	font-size: 5rem;
	font-weight: 200;
	letter-spacing: -0.05em;
	line-height: 1;
	color: rebeccapurple;
	text-align: center;
}

p,
q {
	max-width: 45rem;
	margin: 1rem auto;
	padding: 0 1rem;
	line-height: 1.6;
	text-align: justify;
	shape-outside: circle(50%);
}

q {
	display: block;
	font-size: 2rem;
	font-style: italic;
	text-align: center;
	color: purple;
	text-indent: 2rem hanging each-line;
}

figure {
	width: 200px;
	float: left;
	margin: 0.5rem 150px;

	&:nth-of-type(2) {
		float: right;
	}

	& img {
		border-radius: 50%;
	}

	& figcaption {
		color: rebeccapurple;
		font-style: italic;
		text-align: center;
	}
}

/* ===== Scrollbar CSS ===== */
/* Firefox */
* {
	scrollbar-width: thin;
	scrollbar-color: #8f54a0 #ffffff;
}

/* Chrome, Edge, and Safari */
*::-webkit-scrollbar {
	width: 5px;
}

*::-webkit-scrollbar-track {
	background: #dfdbe5;
}

*::-webkit-scrollbar-thumb {
	background-color: rebeccapurple;
	border-radius: 5px;
}

#topBtn {
	display: none;

	&:hover {
		background-color: white;
		color: rebeccapurple;
		scale: 1.8;
		cursor: pointer;
	}
}

